class PatchError(Exception):
    def __init__(self, filename: str, exit_code: int, cmd: list[str]):
        super().__init__(f"patch failed for {filename}")
        self.filename = filename
        self.exit_code = exit_code
        self.cmd = cmd


def file_sha256(file: str) -> str:
    sha256 = hashlib.sha256()
    with file.open("rb") as f:
        while chunk := f.read(8192):
            sha256.update(chunk)
    return sha256.hexdigest()


def setup_args() -> None:
    parser = argparse.ArgumentParser(
        description="Automagically patch the We Love Katamari Reroll + Royal Reverie with Audio Mod Overhaul"
    )

    parser.add_argument(
        "-v", "--verbose", action="store_true", help="Enable verbose / debug output"
    )

    sub = parser.add_subparsers(
        dest="command", required=True, help="sub-command to run"
    )

    apply_p = sub.add_parser("apply", help="Apply the mod")
    sub.add_parser("remove", help="Remove the mod")
    sub.add_parser("cleanup", help="Cleanup cached asset files")

    apply_p.add_argument(
        "-l",
        "--language",
        default="JP",
        choices=["US", "JP"],
        help="Which region patches to apply (default: JP)",
    )

    return parser.parse_args()


def download_and_verify(url: str, destination: str) -> bool:
    dest_path = pathlib.Path(destination)

    class _ctx:  # tiny namespace for the hook
        start = None
        last_print = 0

    def _progress(block_num: int, block_size: int, total_size: int) -> None:
        if _ctx.start is None:
            _ctx.start = time.time()
        now = time.time()

        # throttle console updates
        if now - _ctx.last_print < 0.2 and block_num * block_size < total_size:
            return
        _ctx.last_print = now

        done = min(total_size, block_num * block_size)
        pct = done * 100 // total_size
        bar = "" * (pct // 2)

        elapsed = now - _ctx.start
        if done and elapsed:
            eta = int((total_size - done) / (done / elapsed))
            eta_str = f"{eta // 60:02d}:{eta % 60:02d}"
        else:
            eta_str = "--:--"

        print(
            f"\r[{bar:<50}] {MAGENTA}{pct:3}%{RESET}  "
            f"{YELLOW}{done >> 20}/{total_size >> 20} MB{RESET}  ETA {BLUE}{eta_str}{RESET}",
            end="",
            flush=True,
        )

    for attempt in range(1, 4):
        try:
            urllib.request.urlretrieve(url, dest_path, reporthook=_progress)
        except Exception as exc:
            print("  ", exc)
        else:
            print()  # newline after bar
            return True
    else:
        print(
            f"{RED}Failed{RESET} to download mod after 3 tries. Aborting the installation."
        )
        sys.exit(1)


def distro_name() -> str:
    """
    Return the pretty distribution name from /etc/os-release.
    Falls back to 'Linux' if the file or field is missing.
    """
    os_release = pathlib.Path("/etc/os-release")
    if not os_release.exists():
        return "Linux"

    data = {}
    for line in os_release.read_text().splitlines():
        if "=" in line and not line.startswith("#"):
            key, value = line.split("=", 1)
            data[key] = value.strip('"')

    return data.get("NAME") or "Linux"


