def list_dboxes() -> list[dict[str, str]]:
    cmd = ["distrobox", "list"]
    completed = subprocess.run(cmd, capture_output=True, text=True, check=True)

    rows = []
    # skip header, split on vertical bar
    for raw in completed.stdout.splitlines()[1:]:
        cid, name, status, image = (part.strip() for part in raw.split("|", 3))
        rows.append({"id": cid, "name": name, "status": status, "image": image})
    return rows


def setup_distrobox(verbose: bool = False) -> bool:
    distrobox_cmd = ["distrobox", "enter", DISTROBOX_NAME, "--"]

    rows = list_dboxes()

    if verbose:
        print(
            f"{BRIGHT_GREEN}Checking{RESET} if there an existing distrobox {BLUE}modding{RESET} container..."
        )
    for r in rows:
        if r["name"] == "modding":
            if verbose:
                print(
                    f"{BRIGHT_GREEN}Found{RESET} distrobox {BLUE}modding{RESET} container"
                )

            cmd = distrobox_cmd + ["xdelta3", "-V"]

            if verbose:
                print(
                    f"{BRIGHT_GREEN}Checking{RESET} if {BLUE}xdelta3{RESET} is installed in modding container..."
                )

            try:
                if subprocess.run(
                    cmd,
                    check=True,
                    stdout=subprocess.DEVNULL,
                    stderr=subprocess.DEVNULL,
                ):

                    print(f"Using {MAGENTA}cached{RESET} distrobox container")
                    return True
                else:
                    if verbose:
                        print(
                            f"{BLUE}xdelta3{RESET} {BRIGHT_GREEN}not installed{RESET} in modding container. Continuing with regular distrobox setup..."
                        )
            except Exception:  # noqa: BLE001
                if verbose:
                    print(
                        f"Something {BRIGHT_GREEN}gone wrong{RESET} when checking the {BLUE}modding{RESET} container. Removing it."
                    )
                    destroy_distrobox(verbose=verbose)

            break

    print(f"{YELLOW}Creating{RESET} distrobox container (this will take a while)...")
    try:
        subprocess.run(
            [
                "distrobox",
                "create",
                "--yes",
                "--image",
                "ubuntu:24.04",
                "--name",
                DISTROBOX_NAME,
            ],
            check=True,
            stdout=subprocess.DEVNULL if not verbose else None,
            stderr=subprocess.DEVNULL if not verbose else None,
        )

        cmd = distrobox_cmd + ["sudo", "apt", "update", "-y"]
        subprocess.run(
            cmd,
            check=True,
            stdout=subprocess.DEVNULL if not verbose else None,
            stderr=subprocess.DEVNULL if not verbose else None,
        )

    except subprocess.CalledProcessError:
        print(f"{RED}Failed{RESET} to create distrobox. Aborting the installation.")
        destroy_distrobox(verbose=verbose)
        return False

    print(f"{YELLOW}Installing{RESET} xdelta3 in distrobox container...")
    try:
        cmd = distrobox_cmd + ["sudo", "apt", "install", "-y", "xdelta3"]
        subprocess.run(cmd, check=True)

    except subprocess.CalledProcessError:
        print(f"{RED}Failed{RESET} to install xdelta3. Aborting the installation.")
        return False

    return True


def destroy_distrobox(verbose: bool = False) -> bool:
    distrobox_cmd = ["distrobox", "rm", "--yes", "--force", DISTROBOX_NAME]

    try:
        subprocess.run(
            distrobox_cmd,
            check=True,
            stdout=subprocess.DEVNULL if not verbose else None,
            stderr=subprocess.DEVNULL if not verbose else None,
        )
    except subprocess.CalledProcessError:
        print(
            f"{RED}Failed{RESET} to remove distrobox container. You should manually remove it with: {BLUE}distrobox rm --yes --force {DISTROBOX_NAME}{RESET}"
        )
        return False

    return True


