def checks(verbose: bool = False) -> bool:

    # Check if the game even installed
    game_dir = (
        pathlib.Path.home() / ".local/share/Steam/steamapps/common/WLKRR/WLKRR_Data"
    )
    if not game_dir.is_dir():
        print(
            f"Game {RED}not installed{RESET}. Please install it and re-run the script."
        )
        return False

    # Check the dependency
    # For Steam Deck, it setups the distrobox
    if distro_name() == "SteamOS":
        if not setup_distrobox(verbose=verbose):
            return False
    else:
        if shutil.which("xdelta3") is None:
            print(
                f"{RED}xdelta3 not found{RESET}. Please install with your package manager."
            )
            return False

    os.makedirs(DOWNLOAD_FOLDER, exist_ok=True)

    return True


def cleanup(verbose: bool = False) -> bool:
    if distro_name() == "SteamOS":
        # Non-fatal
        print(f"{YELLOW}Deleting{RESET} distrobox container...")
        destroy_distrobox(verbose=verbose)

    # Non-fatal errors
    try:
        print(f"{YELLOW}Removing{RESET} the {BLUE}{DOWNLOAD_FOLDER}{RESET}...")
        shutil.rmtree(DOWNLOAD_FOLDER)
    except FileNotFoundError:
        print(
            f"{RED}Failed{RESET} to remove download folder {BLUE}{DOWNLOAD_FOLDER}{RESET}, it doesn't exist..."
        )
    except PermissionError:
        print(
            f"{RED}Failed{RESET} to remove download folder {BLUE}{DOWNLOAD_FOLDER}{RESET}. Permission denied for some reason. Manual cleanup required."
        )

    return True


def apply_patch(base: str, delta: str, verbose: bool = False) -> None:
    bak = f"{base}.bak"

    if os.path.exists(bak):
        return True  # already patched

    cmd = ["xdelta3", "-d", "-s", bak, delta, base]

    if distro_name() == "SteamOS":
        cmd = ["distrobox", "enter", DISTROBOX_NAME, "--"] + cmd

    if verbose:
        print(f"{BRIGHT_GREEN}Creating backup{RESET} of {BLUE}{base}{RESET}...")

    shutil.move(base, bak)

    if verbose:
        print(f"{BRIGHT_GREEN}Applying{RESET} the patch {BLUE}{delta}{RESET}...")

    try:
        subprocess.run(
            cmd,
            check=True,
            stdout=subprocess.DEVNULL if not verbose else None,
            stderr=subprocess.DEVNULL if not verbose else None,
        )
    except subprocess.CalledProcessError as exc:
        raise PatchError(
            filename=os.path.basename(base),
            exit_code=exc.returncode,
            cmd=cmd,
        ) from exc


def apply_mod(language: str, verbose: bool = False) -> bool:
    dst1 = GAME_DIR / "WLK-SFX-Overhaul.resource"
    dst2 = GAME_DIR / "WLK-SFX-Overhaul2.resource"
    b1 = GAME_DATA / "sound_assets_all_28dea2ad1797500a8635433da2d29a5c.bundle"
    b2 = (
        GAME_DATA
        / "scriptableobject_assets_all_0ff3f5d7e008773609ea80b91100a39f.bundle"
    )
    b3 = (
        GAME_DATA
        / "bgm_assets_sound/bgm/m09_result2_fd216475a916cac2ece2be4e0c1a060b.bundle"
    )

    backups = [b.with_suffix(b.suffix + ".bak") for b in (b1, b2, b3)]
    if dst1.exists() and dst2.exists() and all(b.exists() for b in backups):
        if verbose:
            print(f"Mod {BRIGHT_GREEN}already installed{RESET}")
        return True

    def _undo_copy(path: pathlib.Path) -> None:
        try:
            path.unlink()
        except Exception:
            pass

    def _undo_patch(base: pathlib.Path) -> None:
        bak = base.with_suffix(base.suffix + ".bak")
        if bak.exists():
            try:
                bak.replace(base)
            except Exception:
                pass

    print(f"{YELLOW}Applying{RESET} the mod...")

    undos: List[Callable[[], None]] = []

    try:
        # 1. copy first resource file
        if verbose:
            print(f"{BRIGHT_GREEN}Copying{RESET} the {BLUE}first resource{RESET}...")
        shutil.copy(MOD_PATH / "WLK-SFX-Overhaul.resource", dst1)
        undos.append(lambda: _undo_copy(dst1))

        # 2. copy second resource file
        if verbose:
            print(f"{BRIGHT_GREEN}Copying{RESET} the {BLUE}second resource{RESET}...")
        shutil.copy(MOD_PATH / "WLK-SFX-Overhaul2.resource", dst2)
        undos.append(lambda: _undo_copy(dst2))

        # 3. apply first patch
        apply_patch(
            b1,
            MOD_PATH / f"patches/sound_assets_all_{language}.xdelta",
            verbose=verbose,
        )
        undos.append(lambda: _undo_patch(b1))

        # 4. apply second patch
        apply_patch(
            b2,
            MOD_PATH / f"patches/scriptableobject_assets_all_{language}.xdelta",
            verbose=verbose,
        )
        undos.append(lambda: _undo_patch(b2))

        # 5. apply third patch
        apply_patch(b3, MOD_PATH / "patches/m09_result2.xdelta", verbose=verbose)
        undos.append(lambda: _undo_patch(b3))

    except PatchError as e:
        print(f"{RED}Failed{RESET} to apply {e.filename}. Reverting partial changes...")
        # rollback in reverse order
        for rollback in reversed(undos):
            rollback()
        return False

    return True


def remove_mod(verbose: bool = False) -> None:
    def _silent(func, path):
        try:
            func(path)
        except Exception:  # noqa: BLE001
            pass

    def revert_patch(base: pathlib.Path) -> bool:
        """Restore base from base.bak; return True on success."""
        bak = base.with_suffix(base.suffix + ".bak")  # same dir, adds .bak
        try:
            bak.replace(base)  # atomic on Unix, fails if bak missing
            return True
        except FileNotFoundError:
            return False

    tasks = [
        (
            revert_patch,
            GAME_DATA / "sound_assets_all_28dea2ad1797500a8635433da2d29a5c.bundle",
        ),
        (
            revert_patch,
            GAME_DATA
            / "scriptableobject_assets_all_0ff3f5d7e008773609ea80b91100a39f.bundle",
        ),
        (
            revert_patch,
            GAME_DATA
            / "bgm_assets_sound/bgm/m09_result2_fd216475a916cac2ece2be4e0c1a060b.bundle",
        ),
        (pathlib.Path.unlink, GAME_DIR / "WLK-SFX-Overhaul.resource"),
        (pathlib.Path.unlink, GAME_DIR / "WLK-SFX-Overhaul2.resource"),
    ]

    for func, path in tasks:
        if verbose:
            print(f"{BRIGHT_GREEN}Reverting{RESET} changes from {BLUE}{path}{RESET}")
        _silent(func, path)


def download_mod(verbose: bool = False) -> bool:
    file = DOWNLOAD_FOLDER / "wlkrrr.zip"
    wanted = "WLKR-SFX-Overhaul/"
    prefix_len = len(wanted)

    def mini_helper():
        print(f"{YELLOW}Downloading{RESET} the mod...")

        if not download_and_verify(url=MOD_URL, destination=file):
            os.remove(file)
            print(f"Mod archive is {RED}corrupted{RESET}. Aborting the installation.")
            return False

    if os.path.exists(file):
        if file_sha256(file).lower() == MOD_SHA.lower():
            print(f"Using {MAGENTA}cached{RESET} mod")
        else:
            if not mini_helper():
                return False
    else:
        mini_helper()

    print(f"{YELLOW}Extracting{RESET} the mod...")

    with zipfile.ZipFile(file) as zip:
        for zip_info in zip.infolist():
            # skip the folder entry itself and anything outside it
            if zip_info.is_dir() or not zip_info.filename.startswith(wanted):
                continue

            # remove the top-level folder  flatten structure
            zip_info.filename = zip_info.filename[prefix_len:]
            target = MOD_PATH / zip_info.filename

            if target.exists():
                if verbose:
                    print(
                        f"{BRIGHT_GREEN}Skipping{RESET} extraction of {BLUE}{target}{RESET}"
                    )
                continue

            zip.extract(zip_info, MOD_PATH)
    return True


def main():
    args = setup_args()

    if args.command == "apply":
        if not checks(verbose=args.verbose):
            sys.exit(1)
        if not download_mod(verbose=args.verbose):
            sys.exit(1)
        if not apply_mod(language=args.language, verbose=args.verbose):
            sys.exit(1)

        print(ASCII)
        print(
            f"{BOLD}{GREEN}We're so happy for you! Earth is saved, in the nick of time {RED}{RESET}"
        )
    elif args.command == "remove":
        remove_mod(verbose=args.verbose)
        print(ASCII2)
        print(
            f"{BOLD}{RED}This was not your fault, but Ours for believing in you.{RESET}"
        )
    elif args.command == "cleanup":
        cleanup(verbose=args.verbose)
        print(ASCII3)
        print(f"{MAGENTA}Sending Things to Earth - trees... elephants... hopes... dreams...{RESET}")
    else:
        print(f"{RED}Unknown{RESET} flag - {args.action}")
        sys.exit(1)


if __name__ == "__main__":
    main()
